mapper.insert:
INSERT INTO map.convenience_store (objt_id, fclty_cd, fclty_nm, adres, rn_adres, sgg_cd, emd_cd, ctprvn_cd, telno, fclty_ty, data_yr, x, y, raw, geom, created_at, updated_at) SELECT v.objt_id, v.fclty_cd, v.fclty_nm, v.adres, v.rn_adres, v.sgg_cd, v.emd_cd, v.ctprvn_cd, v.telno, v.fclty_ty, v.data_yr, v.x, v.y, v.raw, CASE WHEN v.x IS NOT NULL AND v.y IS NOT NULL THEN ST_SetSRID(ST_MakePoint(v.x, v.y), 3857) ELSE NULL END AS geom, now(), now() FROM ( VALUES
( CAST(#{i.objtId} AS bigint), #{i.fcltyCd}, #{i.fcltyNm}, #{i.adres}, #{i.rnAdres}, #{i.sggCd}, #{i.emdCd}, #{i.ctprvnCd}, #{i.telno}, #{i.fcltyTy}, CAST(#{i.dataYr} AS integer), CAST(#{i.x} AS double precision), CAST(#{i.y} AS double precision), CAST(#{i.rawJson} AS jsonb) )
) AS v( objt_id, fclty_cd, fclty_nm, adres, rn_adres, sgg_cd, emd_cd, ctprvn_cd, telno, fclty_ty, data_yr, x, y, raw ) LEFT JOIN map.convenience_store cs ON cs.objt_id = v.objt_id WHERE cs.objt_id IS NULL

INSERT INTO map.government_office (objt_id, fclty_ty, fclty_cd, fclty_nm, adres, rn_adres, telno, ctprvn_cd, sgg_cd, emd_cd, x_coord, y_coord, data_yr, geom, created_at, updated_at) SELECT v.objt_id, v.fclty_ty, v.fclty_cd, v.fclty_nm, v.adres, v.rn_adres, v.telno, v.ctprvn_cd, v.sgg_cd, v.emd_cd, v.x_coord, v.y_coord, v.data_yr, CASE WHEN v.x_coord IS NOT NULL AND v.y_coord IS NOT NULL THEN ST_SetSRID(ST_MakePoint(v.x_coord, v.y_coord), 3857) ELSE NULL END AS geom, now(), now() FROM ( VALUES
( #{i.objtId}, #{i.fcltyTy}, #{i.fcltyCd}, #{i.fcltyNm}, #{i.adres}, #{i.rnAdres}, #{i.telno}, #{i.ctprvnCd}, #{i.sggCd}, #{i.emdCd}, CAST(#{i.xCoord} AS double precision), CAST(#{i.yCoord} AS double precision), #{i.dataYr} )
) AS v( objt_id, fclty_ty, fclty_cd, fclty_nm, adres, rn_adres, telno, ctprvn_cd, sgg_cd, emd_cd, x_coord, y_coord, data_yr ) LEFT JOIN map.government_office go ON go.objt_id = v.objt_id WHERE go.objt_id IS NULL
